module AdventOfCode2022_Day06

open NUnit.Framework

let input = 
    ""

let example =
    "mjqjpqmgbljsphdztnvjfqwrcgsmlb"

let findStartOfMarker markerSize = 
    Seq.windowed markerSize
    >> Seq.takeWhile 
        (Seq.distinct
         >> Seq.length
         >> (>) markerSize)
    >> Seq.length
    >> (+) markerSize

[<Test>]
let Day06_Part1 () =    
    Assert.AreEqual(1538, findStartOfMarker 4 input)
    

[<Test>]
let Day06_Part2 () =
    Assert.AreEqual(2315, findStartOfMarker 14 input)